mapper.select:

SELECT EXISTS
(
  SELECT 1 FROM NC.reference1
  WHERE dictionary = #{dictionary}
)


SELECT EXISTS
(
  SELECT 1 FROM NC.reference1
  WHERE dictionary = #{dictionary}
   AND content not like '//%//'
)

SELECT * FROM NC.reference1
WHERE content like CONCAT('%', #{query}, '%')
AND dictionary = #{dictionary}


SELECT *
FROM NC.reference1
WHERE dictionary = #{dictionary}
AND sort BETWEEN
(
    SELECT MAX(sort) FROM NC.reference1
    WHERE dictionary = #{dictionary}
    AND sort <=
    (
      SELECT sort FROM NC.reference1
      WHERE dictionary = #{dictionary}
      AND sort = #{sort}
      LIMIT 1
    )
    AND content LIKE '%/FRONT/%'
)
AND
(
    SELECT MIN(sort) FROM NC.reference1
    WHERE dictionary = #{dictionary}
    AND sort >=
    (
       SELECT sort FROM NC.reference1
       WHERE dictionary = #{dictionary}
       AND sort = #{sort}
       LIMIT 1
    )
    AND content LIKE '%/END/%'
)
ORDER BY sort;

SELECT * FROM NC.reference1
WHERE dictionary = #{dictionary}
AND sort ${before ? '<' : '>'} #{sort}
ORDER BY sort ${before ? 'DESC' : 'ASC'}
LIMIT 1

mapper.update:
UPDATE NC.reference1
SET page = #{page}
WHERE dictionary = #{dictionary}
AND sort = #{sort}

mapper.delete:
DELETE FROM NC.reference1 WHERE
(sort > #{frontSort} AND #{endSort} > sort )
AND dictionary = #{dictionary}
